import Genre from './Genre';
import styles from './Action.module.css';

const Action = (props) => {
  useEffect(() => {
    props.fetchActionMovies(1);
    props.fetchActionMovies(2);
    props.fetchActionMovies(3);
  }, []);

  return (
    <div className={styles.action}>
      <div className={styles.title}>
        <span>Movies &gt; </span>
        <h3 className={styles.genre}>Action</h3>
      </div>

      <div className={styles.container}>{/* <Genre /> */}</div>
    </div>
  );
};

