import React from 'react';
import tmdb from '../../apis/tmdb';
import styles from './Card.module.css';

const Card = (props) => {
  const handleSearchDetail = () => {
    const searchDetail = async () => {
      const response = await tmdb.get(
        '/movie/550?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      console.log(response.data);
    };

    searchDetail();
  };

  const renderMovies = () => {
    return props.movies.map((movie) => {
      return (
        <li
          key={movie.id}
          className={`${styles.moviecard} ${styles[props.cname]}`}
          onClick={handleSearchDetail}
        >
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={
                props.cname
                  ? `https://image.tmdb.org/t/p/original${movie.backdrop_path}`
                  : `https://image.tmdb.org/t/p/original${movie.poster_path}`
              }
              alt=""
            />
          </div>
          {props.cname ? (
            ''
          ) : (
            <p className={styles.title}>{movie.original_title}</p>
          )}
          {props.cname ? (
            ''
          ) : (
            <p className={styles.date}>{movie.release_date}</p>
          )}
        </li>
      );
    });
  };

  return <React.Fragment>{renderMovies()}</React.Fragment>;
};

export default Card;

