import React from 'react';
import tmdb from '../../apis/tmdb';
import MovieList from './MovieList';
import styles from './MovieRow.module.css';

class MovieRowWide extends React.Component {
  state = { movies: [] };

  componentDidMount() {
    const fetchData = async () => {
      // try to fetch only english movies
      const response = await tmdb.get(this.props.url);

      console.log(response.data.results);
      this.setState({ movies: response.data.results });
    };

    fetchData();
  }

  render() {
    return (
      <div className={styles.container}>
        <p className={styles.category}>{this.props.category}</p>
        <div className={styles.contents}>
          {/* Can create moviecard component */}
          <ul className={styles.wrapper}>
            <MovieList movies={this.state.movies} />
          </ul>
        </div>
      </div>
    );
  }
}

export default MovieRowWide;
