import React, { useState, useEffect } from 'react';
import * as IoIcons from 'react-icons/io';
import tmdb from '../apis/tmdb';
import styles from './Detail.module.css';

const Detail = () => {
  const [selectedMovie, setSelectedMovie] = useState([]);

  useEffect(() => {
    const fetchDetail = async () => {
      const response = await tmdb.get(
        '/movie/550?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      // console.log(response.data);
      setSelectedMovie(response.data);
    };

    fetchDetail();
  }, []);

  return (
    <div className={styles.item}>
      <h3 className={styles.nav}>Movies</h3>
      <div className={styles.container}>
        <div className={styles.detail}>
          <div className={styles.fig}>
            <img
              src={`https://image.tmdb.org/t/p/original${selectedMovie.poster_path}`}
              alt={selectedMovie.original_title}
              className={styles.img}
            />
          </div>
          <div className={styles.content}>
            <h4 className={styles.title}>{selectedMovie.original_title}</h4>
            <p className={styles.date}>
              {selectedMovie.release_date} / {selectedMovie.runtime}
            </p>
            {/* <p className={styles.runtime}>{selectedMovie.runtime}</p> */}
            <p>genre</p>

            <p className={styles.overview}>{selectedMovie.overview}</p>
            <div className={styles.others}>
              <button className={styles['item__play']}>
                <span>
                  <IoIcons.IoIosPlay />
                </span>
                Play
              </button>
              <a href={selectedMovie.homepage} className={styles.link}>
                Website
              </a>
            </div>
          </div>
        </div>
        <div className={styles.cast}></div>
        <div className={styles.related}></div>
      </div>
    </div>
  );
};

export default Detail;
