import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as FaIcons from 'react-icons/fa';
import * as AiIcons from 'react-icons/ai';
import { IconContext } from 'react-icons';
import SidebarData from './SidebarData';
import SubMenu from './SubMenu';
import styles from './Navbar.module.css';

const Navbar = (props) => {
  const [sidebar, setSidebar] = useState(false);
  const [subnav, setSubnav] = useState(false);

  const showBar = (item) => {
    if (!item.subNav) setSidebar(!sidebar);
    if (item.subNav) setSubnav(!subnav);
  };

  // const showSidebar = () => {
  //   setSidebar(!sidebar);
  // };

  // const showSubnav = () => {
  //   setSubnav(!subnav);
  // };

  // Try to close sidebar also when clicking somewhere in a body element using document.addEventListener('click, {})

  return (
    <IconContext.Provider value={{ color: '#f7f7f7' }}>
      <div className={styles.navbar}>
        <Link to="#" className={styles.menu}>
          <FaIcons.FaBars
            onClick={showSidebar}
            className={styles['menu-icon']}
          />
        </Link>
        {props.children}
      </div>
      <nav
        className={
          sidebar ? `${styles.sidebar} ${styles.active}` : styles.sidebar
        }
      >
        <div className={styles.toggle}>
          <Link to="#" className={styles.menu}>
            <AiIcons.AiOutlineClose
              onClick={showSidebar}
              className={styles['menu-icon']}
            />
          </Link>
        </div>
        <ul className={styles.items}>
          {SidebarData.map((item, index) => {
            return (
              <React.Fragment>
                <li
                  key={index}
                  className={styles[item.className]}
                  // onClick={!item.subNav ? showSidebar : null}
                >
                  <Link to={item.path} className={styles.mainmenu}>
                    {item.icon}
                    <span className={styles.maintitle}>{item.title}</span>
                    <div
                      className={styles.dropdown}
                      onClick={() => showBar(item)}
                    >
                      {/* {item.subNav ? item.iconClosed : null} */}
                      {item.subNav && subnav
                        ? item.iconOpened
                        : item.subNav
                        ? item.iconClosed
                        : null}
                    </div>
                  </Link>

                  <ul className={styles.subitems}>
                    {/* <li className={styles.sublist}>
                      <Link
                        to="/movies/action"
                        key="key"
                        className={styles.submenu}
                      >
                        <span className={styles.label}>Action</span>
                      </Link>
                    </li>
                    <li className={styles.sublist}>
                      <Link
                        to="/movies/adventure"
                        key="key"
                        className={styles.submenu}
                      >
                        <span className={styles.label}>Adventure</span>
                      </Link>
                    </li> */}
                    {/* {subnav && <SubMenu item={item} />} */}
                    {<SubMenu item={item} />}
                  </ul>

                  {/* {subnav &&
                  item.subNav.map((sub, index) => {
                    return (
                      <Link
                        to={sub.path}
                        key={index}
                        className={styles.submenu}
                      >
                        {sub.icon}
                        <span className={styles.label}>{sub.title}</span>
                      </Link>
                    );
                  })} */}
                </li>

                {/* <SubMenu item={item} /> */}
              </React.Fragment>
            );
          })}
        </ul>
      </nav>
    </IconContext.Provider>
  );
};

export default Navbar;
