import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchActionMovies } from '../../../actions';
import styles from './Action.module.css';

const Action = (props) => {
  useEffect(() => {
    props.fetchActionMovies();
  }, []);

  if (!props.movies) {
    return <div>Loading...</div>;
  }

  return (
    <div className={styles.action}>
      <div className={styles.title}>
        <span>Movies &gt; </span>
        <h3 className={styles.genre}>Action</h3>
      </div>

      <div className={styles.container}>
        <div className={styles.content}>
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original/${props.movies[0].poster_path}`}
              alt=""
            />
          </div>
          <div className={styles.description}>
            <p>{props.movies[0].original_title}</p>
            <p>{props.movies[0].release_date}</p>
          </div>
        </div>
        <div className={styles.content}>
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original/${props.movies[0].poster_path}`}
              alt=""
            />
          </div>
          <div className={styles.description}>
            <p>{props.movies[0].original_title}</p>
            <p>{props.movies[0].release_date}</p>
          </div>
        </div>
        <div className={styles.content}>
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original/${props.movies[0].poster_path}`}
              alt=""
            />
          </div>
          <div className={styles.description}>
            <p>{props.movies[0].original_title}</p>
            <p>{props.movies[0].release_date}</p>
          </div>
        </div>
        <div className={styles.content}>
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original/${props.movies[0].poster_path}`}
              alt=""
            />
          </div>
          <div className={styles.description}>
            <p>{props.movies[0].original_title}</p>
            <p>{props.movies[0].release_date}</p>
          </div>
        </div>
        <div className={styles.content}>
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original/${props.movies[0].poster_path}`}
              alt=""
            />
          </div>
          <div className={styles.description}>
            <p>{props.movies[0].original_title}</p>
            <p>{props.movies[0].release_date}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return { movies: state.movies.action };
};

export default connect(mapStateToProps, {
  fetchActionMovies: fetchActionMovies,
})(Action);

