import React, { useState, useEffect } from 'react';
import tmdb from '../apis/tmdb';
import styles from './Detail.module.css';

const Detail = () => {
  const [selectedMovie, setSelectedMovie] = useState([]);

  useEffect(() => {
    const fetchDetail = async () => {
      const response = await tmdb.get(
        '/movie/550?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      // console.log(response.data);
      setSelectedMovie(response.data);
    };

    fetchDetail();
  }, []);

  return (
    <div>
      <h3 className={styles['item-nav']}>Movies</h3>
      <div className={styles.item__container}>
        <div className={styles['item__detail']}>
          <div className={styles['item__fig']}>
            <img
              src={`https://image.tmdb.org/t/p/original${selectedMovie.poster_path}`}
              alt={selectedMovie.original_title}
              className={styles['item__img']}
            />
          </div>
          <div className={styles['item__content']}>
            <h4 className={styles['item__title']}>
              {selectedMovie.original_title}
            </h4>
            <p className={styles['item__date']}>{selectedMovie.release_date}</p>
            <p className={styles['item__runtime']}>{selectedMovie.runtime}</p>
            {/* <p>{selectedMovie.genres.name}</p> */}
            <p className={styles['item__overview']}>{selectedMovie.overview}</p>
          </div>
        </div>
        <div className={styles['item__cast']}></div>
        <div className={styles['item__related']}></div>
      </div>
    </div>
  );
};

export default Detail;
