import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import MovieRow from '../components/movies/MovieRow';
import styles from './Search.module.css';

const Search = (props) => {
  // Should call action creator somewhere upper level than here?
  // useEffect(() => {
  //   props.searchMovies();
  //   props.searchTvShows();
  // }, []);

  const renderMovies = () => {
    if (!props.movies) return <div>Loading...</div>;

    return <p>{props.movies[0].original_title}</p>;
  };

  return (
    <div className={styles.search}>
      <h3 className={styles.title}>Search results</h3>
      {renderMovies()}
      <MovieRow category="Movies" type="toprated" />
    </div>
  );
};

const mapStateToProps = (state) => {
  console.log(state.movies);
  return {
    movies: state.movies.search,
  };
};

export default connect(mapStateToProps)(Search);
