import React from 'react';
import * as IoIcons from 'react-icons/io';
import styles from './Reviews.module.css';

const Reviews = ({ reviews }) => {
  const renderReviews = () => {
    return reviews.map((review) => {
      return (
        <li>
          <div>
            <IoIcons.IoIosPerson />
            <p>{review.author}</p>
          </div>
          <p>{review.content}</p>
        </li>
      );
    });
  };

  return (
    <React.Fragment>
      <p>Reviews</p>
      <ul className={styles.review}>{renderReviews()}</ul>
    </React.Fragment>
  );
};

export default Reviews;
