import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './SubMenu.module.css';

const SubMenu = ({ item, index, onShowSidebar, onShowSubnav, onShowBar }) => {
  const ref = useRef();
  const [sidebar, setSidebar] = useState(false);
  const [subnav, setSubnav] = useState(false);

  const showSidebar = () => {
    setSidebar(!sidebar);
  };

  const showSubnav = () => {
    setSubnav(!subnav);
  };

  // const showBar = (item) => {
  //   if (item.subNav) {
  //     showSubnav();
  //   } else {
  //     showSidebar();
  //   }
  // };

  // const showSubmenu = (item) => {
  //   if (!item.subNav) return null;

  //   return item.subNav.map((sub, index) => {
  //     return (
  //       <li className={styles.sublist}>
  //         <Link
  //           to={sub.path}
  //           key={index}
  //           className={styles.submenu}
  //           onClick={onShowSidebar}
  //         >
  //           <span className={styles.label}>{sub.title}</span>
  //         </Link>
  //       </li>
  //     );
  //   })
  // }

  // console.log(ref.current.children);

  return (
    <li key={index} className={styles[item.className]}>
      <Link
        to={item.path}
        className={styles.mainmenu}
        // onClick={() => onShowBar(item)}
        onClick={item.subNav && showSubnav}
        ref={ref}
      >
        {item.icon}
        <span className={styles.maintitle}>{item.title}</span>
        <div className={styles.dropdown}>
          {item.subNav && subnav
            ? item.iconOpened
            : item.subNav
            ? item.iconClosed
            : null}
        </div>
      </Link>

      {subnav && (
        <ul className={styles.subitems}>
          {item.subNav?.map((sub, index) => {
            return (
              <li className={styles.sublist}>
                <Link
                  to={sub.path}
                  key={index}
                  className={styles.submenu}
                  // onClick={onShowSidebar}
                >
                  <span className={styles.label}>{sub.title}</span>
                </Link>
              </li>
            );
          })}
        </ul>
      )}
    </li>
  );
};

export default SubMenu;
