export default (state = {}, action) => {
  switch (action.type) {
    case 'FETCH_ACTION':
      return { ...state, action: action.payload };

    case 'FETCH_ADVENTURE':
      return { ...state, adventure: action.payload };

    default:
      return state;
  }
};
