import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as IoIcons from 'react-icons/io';
import { searchMovies } from '../actions';
import Navbar from './Navbar';
import styles from './Header.module.css';

const Header = (props) => {
  const [term, setTerm] = useState('');
  const [debouncedTerm, setDebouncedTerm] = useState(term);

  // console.log(term);

  // useEffect(() => {
  //   props.searchMovies();
  // }, []);

  const onSearchTerm = (e) => {
    e.preventDefault();
    props.searchMovies();
  };

  return (
    <Navbar>
      <Link to="/" className={styles.title}>
        Popcorn Club
      </Link>
      <form className={styles.form} onSubmit={onSearchTerm}>
        <input
          className={styles.input}
          type="text"
          placeholder="Search..."
          value={term}
          onChange={(e) => setTerm(e.target.value)}
        />
        <button className={styles.btn}>
          <IoIcons.IoIosSearch className={styles['btn-icon']} />
        </button>
      </form>
      {/* <div className={styles.logout}>

        <button className={styles['logout-btn']}>Log-out</button>
      </div> */}
    </Navbar>
    // Might be a think border under navbar to divide with a main content.
  );
};

export default connect(null, {
  searchMovies: searchMovies,
})(Header);
