import React from 'react';
import styles from './Related.module.css';

const Related = ({ reviews }) => {
  const renderRelated = () => {
    return reviews.map((review) => {
      return (
        <li className={styles.item}>
          <div>
            <IoIcons.IoIosPerson />
            <p>{review.author}</p>
          </div>
          <p>{review.content}</p>
        </li>
      );
    });
  };

  return (
    <React.Fragment>
      <p className={styles.title}>Reviews</p>
      <ul className={styles.review}>{renderRelated()}</ul>
    </React.Fragment>
  );
};

export default Related;
