import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './Sidebar.module.css';

const Sidebar = ({ item }) => {
  const [subnav, setSubnav] = useState(false);

  const showSubnav = () => {
    setSubnav(!subnav);
  };

  return (
    <Link to={item.path} onClick={item.subNav && showSubnav}>
      {item.icon}
      <span className={styles.span}>{item.title}</span>
      {item.subNav && subnav
        ? item.iconOpened
        : item.subNav
        ? item.iconClosed
        : null}
      {item.subNav && subnav
        ? item.iconOpened
        : item.subNav
        ? item.iconClosed
        : null}
    </Link>
  );
};

export default Sidebar;
