import React from 'react';
import { connect } from 'react-redux';
import styles from './HomeFavorite.module.css';

const HomeFavorite = () => {
  return (
    <div className={styles.content}>
      <div className={styles.content1}>
        <Link
          to={`/detail/${movie.id}`}
          key={movie.id}
          className={`${styles.card} ${styles[props.cname]}`}
        >
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={
                props.cname
                  ? `https://image.tmdb.org/t/p/original${movie.backdrop_path}`
                  : `https://image.tmdb.org/t/p/original${movie.poster_path}`
              }
              alt={movie.original_title}
            />
          </div>
          {props.cname ? (
            ''
          ) : (
            <p className={styles.title}>{movie.original_title}</p>
          )}
          {props.cname ? (
            ''
          ) : (
            <p className={styles.date}>{calcYear(movie.release_date)}</p>
          )}
        </Link>
      </div>
      <div className={styles.content2}>movie2</div>
      <div className={styles.content3}>movie3</div>
      <div className={styles.subcontent}>
        <div className={styles.subcontent1}>movie4</div>
        <div className={styles.subcontent2}>movie5</div>
        <div className={styles.subcontent3}>movie6</div>
        <div className={styles.subcontent4}>movie7</div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    shows: state.movies.favorite,
  };
};

export default connect(mapStateToProps)(HomeFavorite);
