import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './SubMenu.module.css';

const SubMenu = ({ item, index }) => {
  // if (!item.subNav) {
  //   return null;
  // }

  // return item.subNav.map((sub, index) => {
  //   return (
  //     <li className={styles.sublist}>
  //       <Link to={sub.path} key={index} className={styles.submenu}>
  //         <span className={styles.label}>{sub.title}</span>
  //       </Link>
  //     </li>
  //   );
  // });

  const [subnav, setSubnav] = useState(false);

  const showSubnav = () => {
    setSubnav(!subnav);
  };

  return (
    <li
      key={index}
      className={styles[item.className]}
      // onClick={() => showBar(item)}
    >
      <Link
        to={item.path}
        className={styles.mainmenu}
        onClick={item.subNav && showSubnav}
      >
        {item.icon}
        <span className={styles.maintitle}>{item.title}</span>
        <div className={styles.dropdown}>
          {/* {item.subNav ? item.iconClosed : null} */}
          {item.subNav && subnav
            ? item.iconOpened
            : item.subNav
            ? item.iconClosed
            : null}
        </div>
      </Link>

      {subnav && (
        <ul className={styles.subitems}>
          {item.subNav.map((sub, index) => {
            return (
              <li className={styles.sublist}>
                <Link
                  to={sub.path}
                  key={index}
                  className={styles.submenu}
                  // ref={ref}
                >
                  <span className={styles.label}>{sub.title}</span>
                </Link>
              </li>
            );
          })}
        </ul>
      )}
    </li>
  );
};

export default SubMenu;
