import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import {
  fetchDetail,
  fetchCredits,
  fetchReviews,
  fetchRelated,
} from '../actions';
import * as IoIcons from 'react-icons/io';
// import tmdb from '../apis/tmdb';
import CastWrap from '../components/detail/CastWrap';
import Reviews from '../components/detail/Reviews';
import Related from '../components/detail/Related';
import styles from './Detail.module.css';

const Detail = (props) => {
  // Use useState for setting selected id
  const [selectedMovie, setSelectedMovie] = useState(550);

  useEffect(() => {
    props.fetchDetail();
    // props.fetchCredits();
    // props.fetchReviews();
    // props.fetchRelated();
  }, []);

  // const [selectedMovie, setSelectedMovie] = useState([]);
  // const [cast, setCast] = useState([]);
  // const [reviews, setReviews] = useState([]);
  // const [related, setRelated] = useState([]);

  // useEffect(() => {
  //   const fetchDetail = async () => {
  //     const response = await tmdb.get('/movie/550');

  //     console.log(response.data);
  //     // console.log(response.data.genres[0].name);
  //     setSelectedMovie(response.data);
  //   };

  //   const fetchCredits = async () => {
  //     const response = await tmdb.get('/movie/550/credits');

  //     // console.log(response.data.cast.slice(0, 10));
  //     setCast(response.data.cast.slice(0, 10));
  //   };

  //   const fetchReviews = async () => {
  //     const response = await tmdb.get('/movie/550/reviews');

  //     console.log(response.data.results.slice(0, 3));
  //     setReviews(response.data.results.slice(0, 3));
  //   };

  //   const fetchRelated = async () => {
  //     const response = await tmdb.get('/movie/550/similar');

  //     console.log(response.data.results);
  //     setRelated(response.data.results);
  //   };

  //   fetchDetail();
  //   fetchCredits();
  //   fetchReviews();
  //   fetchRelated();
  // }, []);

  const calcYear = (date) => {
    const year = date.slice(0, 4);
    return year;
  };

  const calcHour = (runtime) => {
    // 139 -> 2hr 19min
    const hr = Math.floor(runtime / 60);
    const min = runtime % 60;
    return `${hr} hr ${min} min`;
  };

  if (!props.detail) {
    return <div>Loading...</div>;
  }

  return (
    <div className={styles.item}>
      <h3 className={styles.nav}>Movies</h3>
      <div className={styles.container}>
        <div className={styles.detail}>
          <div className={styles.fig}>
            <img
              src={`https://image.tmdb.org/t/p/original${props.detail.poster_path}`}
              alt={props.detail.original_title}
              className={styles.img}
            />
          </div>
          <div className={styles.content}>
            <div className={styles.title}>
              <h4 className={styles.titleName}>
                {props.detail.original_title}
              </h4>
              <button className={styles.favorite}>
                <IoIcons.IoIosHeart className={styles['favorite-icon']} />
              </button>
            </div>
            <p className={styles.date}>{props.detail.release_date}</p>
            <div className={styles.timerate}>
              <p className={styles.runtime}>
                {`${calcHour(props.detail.runtime)}`}
              </p>
              <p className={styles.rate}>
                <span>
                  <IoIcons.IoIosStar />
                </span>
                {props.detail.vote_average}
              </p>
            </div>

            {/* Have to consider when there are multiple genres */}
            {/* <p className={styles.genre}>{selectedMovie.genres[0].name}</p> */}

            <div className={styles.others}>
              <button className={styles.play}>
                <span>
                  <IoIcons.IoIosPlay />
                </span>
                Play
              </button>
              <button href={props.detail.homepage} className={styles.link}>
                <span>
                  <IoIcons.IoIosLink />
                </span>
                Website
              </button>
            </div>
            <p className={styles.overview}>{props.detail.overview}</p>
          </div>
        </div>
        <div className={styles.cast}>{/* <CastWrap /> */}</div>
        <div className={styles.reviews}>{/* <Reviews /> */}</div>
        <div className={styles.related}>{/* <Related /> */}</div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  console.log(state.detail.detail);
  return {
    detail: state.detail.detail,
  };
};

export default connect(mapStateToProps, {
  fetchDetail: fetchDetail,
  // fetchCredits: fetchCredits,
  // fetchReviews: fetchReviews,
  // fetchRelated: fetchRelated,
})(Detail);
