import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as FaIcons from 'react-icons/fa';
import * as AiIcons from 'react-icons/ai';
import { IconContext } from 'react-icons';
import SidebarData from './SidebarData';
import styles from './Navbar.module.css';

const Navbar = (props) => {
  const [sidebar, setSidebar] = useState(false);

  const showSidebar = () => {
    setSidebar(!sidebar);
  };

  // Try to close sidebar also when clicking somewhere in a body element using document.addEventListener('click, {})

  return (
    <IconContext.Provider value={{ color: '#f7f7f7' }}>
      <div className={styles.navbar}>
        {/* Would be better if this can be replaced an cinema icon */}
        <Link to="#" className={styles.menu}>
          <FaIcons.FaBars
            onClick={showSidebar}
            className={styles['menu-icon']}
          />
        </Link>
        {props.children}
      </div>
      <nav
        className={
          sidebar ? `${styles.sidebar} ${styles.active}` : styles.sidebar
        }
      >
        <ul className={styles.items}>
          <li className={styles.toggle} onClick={showSidebar}>
            <Link to="#" className={styles['navbar__menu']}>
              <AiIcons.AiOutlineClose className={styles['navbar__menu-icon']} />
            </Link>
          </li>
          {SidebarData.map((item, index) => {
            return (
              <li
                key={index}
                className={styles[item.className]}
                onClick={showSidebar}
              >
                <Link to={item.path}>
                  {item.icon}
                  <span>{item.title}</span>
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
    </IconContext.Provider>
  );
};

export default Navbar;
