import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import * as FaIcons from 'react-icons/fa';
import * as AiIcons from 'react-icons/ai';
import { IconContext } from 'react-icons';
import SidebarData from './SidebarData';
import SubMenu from './SubMenu';
import styles from './Navbar.module.css';

const Navbar = (props) => {
  const [sidebar, setSidebar] = useState(false);
  const [subnav, setSubnav] = useState(false);
  const ref = useRef();

  const showSidebar = () => {
    setSidebar(!sidebar);
  };

  const showSubnav = () => {
    setSubnav(!subnav);
  };

  // const showBar = (item) => {
  //   if (!item.subNav) showSidebar();
  //   if (item.subNav) showSubnav();
  // };

  // Try to close sidebar also when clicking somewhere in a body element using document.addEventListener('click, {})

  const renderSubnav = (item) => {
    if (!item.subNav) {
      return null;
    }

    return item.subNav.map((sub, index) => {
      return (
        <li className={styles.sublist}>
          <Link to={sub.path} key={index} className={styles.submenu} ref={ref}>
            <span className={styles.label}>{sub.title}</span>
          </Link>
        </li>
      );
    });
  };

  console.log(ref.current);

  return (
    <IconContext.Provider value={{ color: '#f7f7f7' }}>
      <div className={styles.navbar}>
        <Link to="#" className={styles.menu}>
          <FaIcons.FaBars
            onClick={showSidebar}
            className={styles['menu-icon']}
          />
        </Link>
        {props.children}
      </div>
      <nav
        className={
          sidebar ? `${styles.sidebar} ${styles.active}` : styles.sidebar
        }
      >
        <div className={styles.toggle}>
          <Link to="#" className={styles.menu}>
            <AiIcons.AiOutlineClose
              onClick={showSidebar}
              className={styles['menu-icon']}
            />
          </Link>
        </div>
        <ul className={styles.items}>
          {SidebarData.map((item, index) => {
            return <SubMenu item={item} key={index} />;
          })}
        </ul>
      </nav>
    </IconContext.Provider>
  );
};

export default Navbar;
