import React from 'react';
import { connect } from 'react-redux';
import styles from './Related.module.css';

const Related = (props) => {
  const renderRelated = () => {
    if (!props.related) {
      return <div>Loading..</div>;
    }

    return props.related.map((item) => {
      return (
        <li className={styles.item}>
          <div className={styles.fig}>
            <img
              src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
              alt={
                props.type === 'tv' ? item.original_name : item.original_title
              }
              className={styles.img}
            />
          </div>
          <p className={styles.name}>{item.original_title}</p>
        </li>
      );
    });
  };

  return (
    <React.Fragment>
      <p className={styles.title}>Related</p>
      <ul className={styles.related}>{renderRelated()}</ul>
    </React.Fragment>
  );
};

const mapStateToProps = (state) => {
  return {
    related: state.detail.related,
  };
};

export default connect(mapStateToProps)(Related);
