import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import * as IoIcons from 'react-icons/io';
import {
  fetchMovieDetail,
  fetchMovieCredits,
  fetchMovieReviews,
  fetchMovieRelated,
} from '../actions';
import CastWrap from '../components/detail/Cast';
import Reviews from '../components/detail/Reviews';
import Related from '../components/detail/Related';
import styles from './Detail.module.css';

const Detail = (props) => {
  // Use useState for setting selected id
  const [selectedMovie, setSelectedMovie] = useState(550);

  // Also movies added in the favorite could be stored in state. So, use redux for that?

  useEffect(() => {
    props.fetchMovieDetail(selectedMovie);
    props.fetchMovieCredits(selectedMovie);
    props.fetchMovieReviews(selectedMovie);
    props.fetchMovieRelated(selectedMovie);
  }, []);

  const calcYear = (date) => {
    // const year = date.slice(0, 4);
    // return year;
    const year = date.split('-')[0];
    return year;
  };

  const calcHour = (runtime) => {
    // 139 -> 2hr 19min
    const hr = Math.floor(runtime / 60);
    const min = runtime % 60;
    return `${hr} hr ${min} min`;
  };

  if (!props.detail) {
    return <div>Loading...</div>;
  }

  console.log(props);

  return (
    <div className={styles.item}>
      <h3 className={styles.nav}>Movies</h3>
      <div className={styles.container}>
        <div className={styles.detail}>
          <div className={styles.fig}>
            <img
              src={`https://image.tmdb.org/t/p/original${props.detail.poster_path}`}
              alt={props.detail.original_title}
              className={styles.img}
            />
          </div>
          <div className={styles.content}>
            <div className={styles.title}>
              <h4 className={styles.titleName}>
                {props.detail.original_title}
              </h4>
              <button className={styles.favorite}>
                <IoIcons.IoIosHeart className={styles['favorite-icon']} />
              </button>
            </div>
            <p className={styles.date}>{calcYear(props.detail.release_date)}</p>
            <div className={styles.timerate}>
              <p className={styles.runtime}>
                {`${calcHour(props.detail.runtime)}`}
              </p>
              <p className={styles.rate}>
                <IoIcons.IoIosStar className={styles['rate-icon']} />
                <p>{props.detail.vote_average}</p>
              </p>
            </div>

            {/* Have to consider when there are multiple genres */}
            {/* <p className={styles.genre}>{selectedMovie.genres[0].name}</p> */}

            <div className={styles.others}>
              <button className={styles.play}>
                <IoIcons.IoIosPlay className={styles['play-icon']} />
                <p>Play</p>
              </button>
              <button href={props.detail.homepage} className={styles.link}>
                <IoIcons.IoIosLink className={styles['link-icon']} />
                <p>Website</p>
              </button>
            </div>
            <p className={styles.overview}>{props.detail.overview}</p>
          </div>
        </div>

        <div className={styles.cast}>
          <CastWrap />
        </div>

        <div className={styles.reviews}>
          <Reviews />
        </div>

        <div className={styles.related}>
          <Related />
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  console.log(state.detail.detail);
  return {
    detail: state.detail.detail,
  };
};

export default connect(mapStateToProps, {
  fetchMovieDetail: fetchMovieDetail,
  fetchMovieCredits: fetchMovieCredits,
  fetchMovieReviews: fetchMovieReviews,
  fetchMovieRelated: fetchMovieRelated,
})(Detail);
