export default (state = [], action) => {
  switch (action.type) {
    case 'MOVIE_NOWPLAYING':
      return [...state, ...action.payload];

    // case 'MOVIE_POPULAR':
    //   return { ...state, popular: action.payload };

    // case 'MOVIE_UPCOMING':
    //   return { ...state, upcoming: action.payload };

    // case 'MOVIE_TOPRATED':
    //   return { ...state, toprated: action.payload };

    default:
      return state;
  }
};
