export default (state = {}, action) => {
  switch (action.type) {
    // case 'MOVIE_NOWPLAYING':
    //   return [...state, ...action.payload];

    case 'MOVIE_NOWPLAYING':
      return { ...state, ...action.payload };

    // case 'MOVIE_POPULAR':
    //   return { ...state, [action.type]: action.payload };

    case 'MOVIE_UPCOMING':
      return [...state, ...action.payload];
    // return { ...state, upcoming: action.payload };

    // case 'MOVIE_TOPRATED':
    //   return { ...state, toprated: action.payload };

    default:
      return state;
  }
};
