import React, { useState, useEffect } from 'react';
import * as AiIcons from 'react-icons/ai';
import tmdb from '../apis/tmdb';
import MovieRow from '../components/movies/MovieRow';
import styles from './Movies.module.css';

const base_url = 'https://image.tmdb.org/t/p/original';
// const url =
//   'https://api.themoviedb.org/3/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6';

const Movies = () => {
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      // try to fetch only english movies
      const response = await tmdb.get(
        '/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      console.log(response.data.results);
      setMovie(response.data.results.slice(0, 3));
    };

    fetchData();
  }, []);

  const slider = () => {
    const slides = document.querySelectorAll('.slide');
    const btnLeft = document.querySelector('.slider__btn--left');
    const btnRight = document.querySelector('.slider__btn--right');

    let curSlide = 0;
    const maxSlide = slides.length;

    const goToSlide = function (slide) {
      slides.forEach(
        (s, i) => (s.style.transform = `translateX(${100 * (i - slide)}%)`)
      );
    };

    const nextSlide = function () {
      if (curSlide === maxSlide - 1) {
        curSlide = 0;
      } else {
        curSlide++;
      }

      goToSlide(curSlide);
    };

    const prevSlide = function () {
      if (curSlide === 0) {
        curSlide = maxSlide - 1;
      } else {
        curSlide--;
      }
      goToSlide(curSlide);
    };

    const init = function () {
      goToSlide(0);
      createDots();
      activateDot(0);
    };
    init();

    // Event handlers
    btnRight.addEventListener('click', nextSlide);
    btnLeft.addEventListener('click', prevSlide);

    document.addEventListener('keydown', function (e) {
      if (e.key === 'ArrowLeft') prevSlide();
      e.key === 'ArrowRight' && nextSlide();
    });
  };
  slider();

  return (
    <div className={styles.movies}>
      <h3 className={styles.title}>Movies</h3>
      <div className={styles.slider}>
        {movie.map((item) => {
          return (
            <div className={styles.slide} style={{ transform: 'translateX()' }}>
              {/* <div key={movie.id} className={styles.hero}> */}
              <div className={styles.img}>
                <img
                  className={styles.poster}
                  src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
                  alt=""
                />
              </div>
              <p className={styles.name}>title</p>
              {/* </div> */}
            </div>
          );
        })}
        <div className={styles.slide} style={{ transform: 'translateX()' }}>
          {/* <div key={movie.id} className={styles.hero}> */}
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
              alt=""
            />
          </div>
          <p className={styles.name}>title</p>
          {/* </div> */}
        </div>
        <div className={styles.slide} style={{ transform: 'translateX()' }}>
          {/* <div key={movie.id} className={styles.hero}> */}
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
              alt=""
            />
          </div>
          <p className={styles.name}>title</p>
          {/* </div> */}
        </div>
        <div className={styles.slide} style={{ transform: 'translateX()' }}>
          {/* <div key={movie.id} className={styles.hero}> */}
          <div className={styles.img}>
            <img
              className={styles.poster}
              src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
              alt=""
            />
          </div>
          <p className={styles.name}>title</p>
          {/* </div> */}
        </div>
        <button className={styles['btn-left']}>
          <AiIcons.AiOutlineLeft className={styles['btn-icon']} />
        </button>
        <button className={styles['btn-right']}>
          <AiIcons.AiOutlineRight className={styles['btn-icon']} />
        </button>
      </div>
      <MovieRow
        url="/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6"
        category="Popular"
      />
      <MovieRow
        url="/movie/top_rated?api_key=8a45061d820fb7b5b5f574766f028ff6"
        category="Top Rated"
      />
      <MovieRow
        url="/movie/upcoming?api_key=8a45061d820fb7b5b5f574766f028ff6"
        category="Upcoming"
      />
      <MovieRow
        url="/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6"
        category="Popular"
      />
    </div>
  );

  // as props: category(popular, toprated...),
};

