import React from 'react';
import styles from './CastWrap.module.css';

const CastWrap = (props) => {
  const renderCast = () => {
    return props.cast.map((person) => {
      return (
        <li key={person.id} className={styles.list}>
          <div className={styles.fig}>
            <img
              src={`https://image.tmdb.org/t/p/original${person.profile_path}`}
              alt={person.original_name}
              className={styles.img}
            />
          </div>
          <p className={styles.name}>{person.original_name}</p>
        </li>
      );
    });
  };

  return (
    <React.Fragment>
      <p>Cast</p>
      <ul className={styles.wrap}>{renderCast()}</ul>
    </React.Fragment>
  );
};

export default CastWrap;
