export default (state = {}, action) => {
  switch (action.type) {
    case 'DETAIL':
      return { ...state, detail: action.payload };

    case 'CREDITS':
      return { ...state, cast: action.payload };

    case 'REVIEWS':
      return { ...state, reviews: action.payload };

    case 'RELATED':
      return { ...state, related: action.payload };

    default:
      return state;
  }
};
