import React from 'react';
import tmdb from '../../apis/tmdb';
import styles from './Popular.module.css';

class Popular extends React.Component {
  state = { movies: [] };

  // componentDidMount() {
  //   const fetchData = async () => {
  //     const response = await tmdb.get(
  //       '/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6'
  //     );

  //     console.log(response.data.results);
  //     this.setState({ movies: response.data.results });
  //   };

  //   fetchData();
  // }
  componentDidMount() {
    const fetchData = async () => {
      const response = await tmdb.get(
        '/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      console.log(response.data.results);
      this.setState({ movies: response.data.results });
    };

    fetchData();
  }

  renderMovies() {
    this.state.movies.map((movie) => {
      return (
        <li key={movie.id} className={styles.moviecard}>
          <div className={styles.img}>
            <img
              className={styles.img__poster}
              src={`https://image.tmdb.org/t/p/original${movie.backdrop_path}`}
              alt=""
            />
          </div>
          <p className={styles.title}>{movie.original_title}</p>
          <p className={styles.date}>{movie.release_date}</p>
        </li>
      );
    });
  }

  render() {
    return (
      <div className={styles.container}>
        <p className={styles.category}>Popular</p>
        <div className={styles.contents}>
          {/* Can create moviecard component */}
          <ul className={styles.contents__wrapper}>{this.renderMovies}</ul>
        </div>
      </div>
    );
  }
}

export default Popular;
