import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import movieTrailer from 'movie-trailer';
import styles from './Modal.module.css';

const Overlay = () => {
  return <div className={styles.backdrop}></div>;
};

const Play = (props) => {
  const [trailerUrl, setTrailerUrl] = useState('');

  // console.log(props);
  const { id } = props.match.params;

  const onPlay = () => {
    movieTrailer(null, { tmdbId: id })
      .then((res) => {
        const url = new URL(res);
        const param = new URLSearchParams(url.search);
        setTrailerUrl(param.get('v'));
      })
      .catch((err) => console.log(err));
  };

  const videoSrc = `https://www.youtube.com/embed/${trailerUrl}`;

  return (
    <div className={styles.modal}>
      <iframe src={videoSrc} width="500" height="400" />
    </div>
  );
};

const Modal = () => {
  return (
    <React.Fragment>
      {ReactDOM.createPortal(<Overlay />, document.getElementById('overlay'))}
      {ReactDOM.createPortal(<Play />, document.getElementById('modal'))}
    </React.Fragment>
  );
};

export default Modal;
