import { combineReducers } from 'redux';

const showsReducer = (state = [], action) => {
  switch (action.type) {
    case MOVIE_NOWPLAYING:
      return action.payload;

    case TV_ONAIR:
      return action.payload;

    default:
      return state;
  }
};

export default combineReducers({
  shows: showsReducer,
});
