import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as FaIcons from 'react-icons/fa';
import * as AiIcons from 'react-icons/ai';
import { IconContext } from 'react-icons';
import SidebarData from './SidebarData';
import styles from './Navbar.module.css';

const Navbar = () => {
  const [sidebar, setSidebar] = useState(false);

  const showSidebar = () => {
    setSidebar(!sidebar);
  };

  // Try to close sidebar also when clicking somewhere in a body element using document.addEventListener('click, {})

  return (
    <div>
      {/* Try to use className here as well  */}
      <IconContext.Provider value={{ color: '#fff' }}>
        <div className={styles.navbar}>
          <Link to="#" className={styles['menu-bars']}>
            <FaIcons.FaBars
              onClick={showSidebar}
              className={styles['menu-bars-icon']}
            />
          </Link>
        </div>
        <nav
          className={
            sidebar
              ? `${styles['nav-menu']} ${styles.active}`
              : styles['nav-menu']
          }
        >
          <ul className={styles['nav-menu-items']} onClick={showSidebar}>
            <li className={styles['navbar-toggle']}>
              <Link to="#" className={styles['menu-bars']}>
                <AiIcons.AiOutlineClose className={styles['menu-bars-icon']} />
              </Link>
            </li>
            {SidebarData.map((item, index) => {
              return (
                <li key={index} className={styles[item.className]}>
                  <Link to={item.path}>
                    {item.icon}
                    <span>{item.title}</span>
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>
      </IconContext.Provider>
    </div>
  );
};

export default Navbar;
