import React from 'react';
import tmdb from '../../apis/tmdb';
import styles from './Popular.module.css';

class Popular extends React.Component {
  state = { movies: [] };

  componentDidMount() {
    const fetchData = async () => {
      const response = await tmdb.get(
        '/movie/popular?api_key=8a45061d820fb7b5b5f574766f028ff6'
      );

      console.log(response.data.results);
      this.setState({ movies: response.data.results[0] });
    };

    fetchData();
  }

  // renderMovies = () => {
  //   this.state.movies.map((movie) => {
  //     <div>
  //       <h3 style={{ color: 'black' }}>{movie.original_title}</h3>
  //       <p style={{ color: 'black' }}>{movie.release_date}</p>
  //     </div>;
  //   });
  // };

  render() {
    // console.log(this.state.movies.original_title);
    return (
      <div className={styles.popular}>
        <img
          className={styles.img}
          src={`https://image.tmdb.org/t/p/original${this.state.movies.backdrop_path}`}
          alt=""
        />
        <h4 className={styles.title}>{this.state.movies.original_title}</h4>
        <p>{this.state.movies.release_date}</p>
      </div>
    );
  }
}

export default Popular;
