export default (state = {}, action) => {
  switch (action.type) {
    case 'SEARCH_MOVIES':
      return { ...state, movies: action.payload };

    case 'SEARCH_TVS':
      return { ...state, shows: action.payload };

    default:
      return state;
  }
};
